package com.hura.find_lost_device1import android.app.Serviceimport android.content.BroadcastReceiverimport android.content.Contextimport android.content.Intentimport android.content.IntentFilterimport android.os.BatteryManagerimport android.os.IBinderimport android.util.Log  class BatteryService : Service() {   override fun onStartCommand(intent: Intent?, flags: Int, startId: Int): Int {        Log.d("MyService", "onStartCommand")        // do not receive all available system information (it is a filter!)        val battChangeFilter = IntentFilter(            Intent.ACTION_BATTERY_CHANGED        )        // register our receiver        this.registerReceiver(BatteryBroadcastReceiver(), battChangeFilter)        return START_STICKY    }    override fun onBind(intent: Intent?): IBinder? {        // There are Bound an Unbound Services - you should read something about        // that. This one is an Unbounded Service.        return null    }}