import 'package:firebase_database/firebase_database.dart';class DeviceModel {  String id;  String deviceModel;  String email;  String name;  String manufactureName;  String deviceToken;  double lat;  double lng;  DeviceModel(this.id, this.deviceModel, this.email, this.name,      this.manufactureName, this.deviceToken, this.lat, this.lng);  DeviceModel.fromSnapshot(DataSnapshot snapshot)      : id = snapshot.key,        deviceModel = snapshot.value["deviceModel"],        email = snapshot.value["email"],        name = snapshot.value["name"],        manufactureName = snapshot.value["manufactureName"],        deviceToken = snapshot.value["deviceToken"],        lat = snapshot.value["lat"],        lng = snapshot.value["lng"];  toJson() {    return {      "id": id,      "deviceModel": deviceModel,      "email": email,      "name": name,      "manufactureName": manufactureName,      "deviceToken": deviceToken,      "lat": lat,      "lng": lng,    };  }}