import 'package:assets_audio_player/assets_audio_player.dart';import 'package:find_lost_device1/screens/dashboard.dart';import 'package:flutter/material.dart';import 'package:flutter/services.dart';class BillStartScreen extends StatefulWidget {  const BillStartScreen({Key key}) : super(key: key);  @override  _BillStartScreen createState() => _BillStartScreen();}class _BillStartScreen extends State<BillStartScreen> {  static const platform1 = const MethodChannel('flutter.native/helper');  Future<void> responseDisable() async {    try {      await platform1.invokeMethod("disable");    } on PlatformException catch (e) {      print("Failed to Invoke: '${e.message}'.");    }    setState(() {      // _responseFromNativeCode1=res;    });  }  @override  void initState() {    responseDisable();    try {      const alarmAudioPath = "assets/sound_alarm.mp3";      AssetsAudioPlayer.newPlayer().open(        Audio(alarmAudioPath),        autoStart: true,        volume: 20.0,        loopMode: LoopMode.playlist,        playInBackground: PlayInBackground.enabled,        respectSilentMode: false,        audioFocusStrategy: AudioFocusStrategy.request(            resumeAfterInterruption: true, resumeOthersPlayersAfterDone: true),        showNotification: false,      );    } catch (e) {      print(e);    }    // TODO: implement initState    super.initState();  }  @override  Widget build(BuildContext context) {    return WillPopScope(        onWillPop: () => Future.value(false),        child: Scaffold(            appBar: AppBar(              title: Text("Find Lost Phone"),            ),            body: Container(                child: Column(                    crossAxisAlignment: CrossAxisAlignment.center,                    children: <Widget>[                  SizedBox(                    height: 10,                  ),                  Center(                    child: GestureDetector(                        onTap: () {                          setState(() {                            Navigator.push(                              context,                              new MaterialPageRoute(                                  builder: (ctxt) => new Dashboard()),                            );                          });                        },                        child: Container(                          padding: EdgeInsets.all(15),                          decoration: BoxDecoration(                            borderRadius: BorderRadius.circular(30),                            color: Colors.amber[700],                          ),                          child: Text(                            "Stop",                            style: TextStyle(fontSize: 20),                          ),                        )),                  )                ]))));  }}