import 'package:flutter/material.dart';import 'package:flutter/services.dart';import 'package:shared_preferences/shared_preferences.dart';class DontTouchScreen extends StatefulWidget {  @override  _DontTouchScreenState createState() => _DontTouchScreenState();}class _DontTouchScreenState extends State<DontTouchScreen> {  Color _backgroundColor = Colors.indigo;  static bool start = false;  SharedPreferences prefs;  static const platform1 = const MethodChannel('flutter.native/helper');  Future<void> responseSensor(bool start) async {    try {      if (start) {        await platform1.invokeMethod("sensorDetectionActive");      } else {        await platform1.invokeMethod("sensorDetectionDeactive");      }    } on PlatformException catch (e) {      print("Failed to Invoke: '${e.message}'.");    }    setState(() {      // _responseFromNativeCode1=res;    });  }  Future<void> prefrenses() async {    prefs = await SharedPreferences.getInstance();    start = prefs.getBool("start") ?? false;    print(start);    setState(() {});  }  @override  void initState() {    // openSensor();    prefrenses();    // TODO: implement initState    super.initState();  }  @override  Widget build(BuildContext context) {    print("d $start");    return Scaffold(      backgroundColor: _backgroundColor,      body: Column(        // mainAxisAlignment: MainAxisAlignment.center,        crossAxisAlignment: CrossAxisAlignment.stretch,        children: <Widget>[          Container(            margin: EdgeInsets.only(                top: (MediaQuery.of(context).size.height) / 2 - 60),            child: Text(              "Don't touch phone",              textAlign: TextAlign.center,              style: TextStyle(                fontSize: 24,                color: Colors.white,              ),            ),          ),          GestureDetector(            onTap: () {              if (start) {                setState(() {                  start = false;                  prefs.setBool("start", start);                  responseSensor(false);                });              } else {                setState(() {                  responseSensor(true);                  start = true;                  prefs.setBool("start", start);                });              }            },            child: Container(              margin: EdgeInsets.only(                  top: (MediaQuery.of(context).size.height / 8)),              padding: EdgeInsets.all(10),              child: Center(                child: Container(                  padding: EdgeInsets.all(15),                  decoration: BoxDecoration(                    borderRadius: BorderRadius.circular(30),                    color: Colors.amber[700],                  ),                  child: Text(                    start ? "Stop" : "Start",                    style: TextStyle(fontSize: 20),                  ),                ),              ),            ),          ),        ],      ),    );  }// void openSensor() {//   accelerometerEvents.listen((AccelerometerEvent event) {//     print(event.x);//     print(event.y);//     print(event.z);//     setState(() {//       if(start) {//         if (event.y < -3 || event.y > 3 || event.x < -6 || event.x > 6) {//           _backgroundColor = Colors.red;//           Navigator.pushReplacement(//             context,//             new MaterialPageRoute(builder: (ctxt) => new BillStartScreen()),//           );//           start=false;//         } else//           _backgroundColor = Colors.indigo;//       }//     });//  });// }}