import 'package:find_lost_device1/screens/dashboard.dart';import 'package:flutter/material.dart';import 'package:splashscreen/splashscreen.dart';class SplashScreens extends StatelessWidget {  @override  Widget build(BuildContext context) {    return SplashScreen(        seconds: 5,        image: Image(          image: AssetImage("assets/flutter_icon.png"          ),          height: MediaQuery.of(context).size.height/3,          width: MediaQuery.of(context).size.width/2,        ),        // imageBackground: ,        // the widget to run after running your splashscreen for 1 sec        navigateAfterSeconds: Dashboard(),        styleTextUnderTheLoader: TextStyle(),        loaderColor: Colors.blue);  }}